import React, { Component } from 'react';
import { v4 as uuid } from 'uuid';
import axios from 'axios';

export default class InitialCall extends Component {
  state = {
    isLoading: true,
    jokes: [],
    joke: '',
    punchline: ''
  };

  async componentDidMount() {
    /**
     * first check if a record exist
     * if it does
     *  retrieve from DB
     *  set the state
     * else
     *  populate DB with all jokes *
     *  set the state *
     */
    axios.get('https://official-joke-api.appspot.com/random_ten')
      .then(res => {
        const allJokes = res.data.map(jokesObj => {
          const jkContent = { // create record
            id: jokesObj.id,
            firstName: jokesObj.setup,
            lastName: jokesObj.punchline,
            email : jokesObj.type
          }
          axios.post('/first_api/v1/employees', jkContent) // post to DB
          return jkContent
        })

        this.setState({
          jokes: allJokes
        })
      })

  }
  onSubmit = (e) => {
    e.preventDefault()
    const joke = {
      id: +uuid(),
      firstName: this.state.joke,
      lastName: this.state.punchline,
      email : 'add general'
    }
    axios.post('/first_api/v1/employees', joke) 
    this.setState({
      joke: '',
      punchline: ''
    })
  }

  onChange = (e) => {
    this.setState({
      [e.target.name] : e.target.value
    })
  }

  editJoke = (id, joke) => {
    console.log(id, joke)
    // const joke = {
    //   id: id,
    //   firstName: joke
    // }

    // axios.put(`/first_api/v1/employees/${id}`)

  }

  // removeJoke = (id) => {

  //    // axios.put(`/first_api/v1/employees/${id}`)
  // }


  render() {
    // const {employees, isLoading} = this.state;
    console.log(this.state)
    // if (isLoading) {
    //   return <p>Loading...</p>;
    // }

    return (

      <div className="Initial">
      <h1>  </h1>

      {
        this.state.jokes.map(item => 
          <p key={item.id} onClick={() => this.editJoke(item.id, item.firstName)}>{item.firstName} </p> 
        )
      }
        <form onSubmit={this.onSubmit}>
          <input 
            type='text' 
            name='joke' 
            value={this.state.joke} 
            placeholder="add a joke"
            onChange={this.onChange}
            />
          <input 
            type='text' 
            name='punchline' 
            value={this.state.punchline} 
            placeholder="add a punchline"
            onChange={this.onChange}
            />
          <button>add</button>
        </form>


      </div>
    );
  }
}
