import React, { Component } from 'react';
import { v4 as uuid } from 'uuid';
import axios from 'axios';


export default class InitialCall extends Component {
  state = {
    isLoading: true,
    allJokes: [],
    joke: '',
    punchline: '',
  };

  async componentDidMount() {
    /**
     * first check if a record exist
     * if it does
     *  retrieve from DB
     *  set the state
     * else
     *  populate DB with all allJokes *
     *  set the state *
     */
    axios.get('https://official-joke-api.appspot.com/random_ten')
      .then(res => {
        const allJokes = res.data.map((jokesObj, idx) => {
          const jkContent = { // create record
            jk_id: idx,
            jk_type: jokesObj.type,
            jokes: jokesObj.setup,
            punch_line: jokesObj.punchline,
          }
          console.log( jkContent.jk_id, 'jhfjhoajnjvnajnejfnjnj')

          axios.post('first_api/v1/jokes', jkContent) // post to DB
          return jkContent
        })

        this.setState({
          allJokes: allJokes,
        })
      })
  }


  onSubmit = (e) => {
    e.preventDefault()
    const joke = {
      jk_id: this.state.allJokes.length + 1,
      jk_type : 'add general', 
      jokes: this.state.joke,
      punch_line: this.state.punchline,
    }
    this.setState({
      joke: '',
      punchline: ''
    })
    axios.post('first_api/v1/jokes', joke) 
  }

  onChange = (e) => {
    this.setState({
      [e.target.name] : e.target.value
    })
  }

  editJoke = (id) => {
    const  newJoke = prompt("update your joke");
    const  newpunchline = prompt("add a punchline");
    if(newJoke && newpunchline) {
      let  joke = {
        jk_id: id,
        jk_type : 'add general',
        jokes: newJoke,
        punch_line: newpunchline,
      }

      axios.put(`first_api/v1/jokes/${id}`,joke ) 
    }
  }

  removeJoke = (id) => {
    // console.log(id)
     axios.delete(`first_api/v1/jokes/1/${id}`)
  }


  render() {
    // const {employees, isLoading} = this.state;
    console.log(this.state)
    // if (isLoading) {
    //   return <p>Loading...</p>;
    // }

    return (
      <div className="Initial">


      {
        this.state.allJokes.map(item =>
        <div key={item.jk_id}> 
          <p onClick={() => this.editJoke(item.id)}>
            {item.jokes} 
          </p> 
          <button onClick={() => this.removeJoke(item.jk_id)}>
            delete
          </button>
        </div>
        )
      }
        <form onSubmit={this.onSubmit}>
          <input 
            type='text' 
            name='joke' 
            value={this.state.joke} 
            placeholder="add a joke"
            onChange={this.onChange}
            />
          <input 
            type='text' 
            name='punchline' 
            value={this.state.punchline} 
            placeholder="add a punchline"
            onChange={this.onChange}
            />
          <button>add</button>
        </form>


      </div>
    );
  }
}
