import React, { Component } from 'react';
import axios from 'axios';

export default class InitialCall extends Component {
  state = {
    isLoading: true,
    jokes: [],
  };

  async componentDidMount() {
    /**
     * first check if a record exist
     * if it does
     *  retrieve from DB
     *  set the state
     * else
     *  populate DB with all jokes *
     *  set the state *
     */
    axios.get('https://official-joke-api.appspot.com/random_ten')
      .then(res => {
        const allJokes = res.data.map(jokesObj => {
          const jkContent = { // create record
            id: jokesObj.id,
            firstName: jokesObj.setup,
            lastName: jokesObj.punchline,
            email : jokesObj.type
          }
          axios.post('/first_api/v1/employees', jkContent) // post to DB
          return jkContent
        })

        this.setState({
          jokes: allJokes
        })
      })

  }
  // createJoke() {

  //   axios.post('/first_api/v1/employees') 

  // }


  render() {
    // const {employees, isLoading} = this.state;
    console.log(this.state)
    // if (isLoading) {
    //   return <p>Loading...</p>;
    // }

    return (

      <div className="Initial">
      <h1>  </h1>

      {
        this.state.jokes.map(item => 
          <p key={item.id}>{item.firstName} </p> 
        )
      }
        <form onSubmit={this.addItem}>
          <input type='text' placeholder="Add Your Joke"/>
          <button onClick={this.createJoke}>add</button>
        </form>
      </div>
    );
  }
}
